import{_ as l,c as i,o as a,a2 as o}from"./chunks/framework.wERSFrVX.js";const u=JSON.parse('{"title":"大大大块牛腩面","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/主食/大大大块牛腩面.md","filePath":"cookbook/主食/大大大块牛腩面.md"}'),t={name:"cookbook/主食/大大大块牛腩面.md"},e=o('<h1 id="大大大块牛腩面" tabindex="-1">大大大块牛腩面 <a class="header-anchor" href="#大大大块牛腩面" aria-label="Permalink to &quot;大大大块牛腩面&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>鲜面</li><li>牛腩</li><li>青菜</li><li>茶叶蛋</li><li>底料（酱油、水、花椒油、大蒜等）（来自成都圣恩生物）</li><li>红油</li><li>熟猪油</li><li>骨汤调味料（水、猪油、白砂糖、生姜、大葱等）（来自成都圣恩生物）</li><li>蒜子</li><li>香菜</li><li>清油火锅底料(大豆油、郫县豆瓣、辣椒、花椒、黑胡椒等）（来自成都圣恩生物）</li><li>姜片</li><li>菜籽油</li><li>白砂糖</li><li>藤椒油</li><li>老抽</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><ul><li><ol><li><p>牛腩烧制：</p><p>①锅中下入120g 菜籽油烧至180℃，下入25g 姜片炒香； ②下入1000g 焯过水的牛腩、25g 大葱段充分煸炒，下入12g 老抽上色； ③下入150g 清油火锅底料炒香，加1300g 水烧开； ④下入4g 盐、3g 白砂糖后倒入高压锅小火压18 分钟焖5 分钟； ⑤开盖捞出姜葱，下入15g 藤椒油，搅拌均匀备用。</p></li></ol></li><li><ol start="2"><li>10 份底汤调制：2400g 开水中加入120g 汤料，搅拌均匀备用；</li></ol></li><li><ol start="3"><li>将130g 鲜面和25g 青菜在沸水中煮2 分钟；</li></ol></li><li><ol start="4"><li>面碗中加入20g 牛肉面汤底料、20g 红油、5g 蒜蓉、5g熟猪油、250g 底汤，放入煮好的面条；</li></ol></li><li><ol start="5"><li>将80g 牛腩和25g 青菜摆放在面条上，加入20g 牛腩汤汁，撒入5g 香菜、5g 葱花出品。</li></ol></li></ul>',5),r=[e];function _(s,c,n,g,d,h){return a(),i("div",null,r)}const m=l(t,[["render",_]]);export{u as __pageData,m as default};
