import{_ as l,c as i,o as a,a2 as o}from"./chunks/framework.wERSFrVX.js";const u=JSON.parse('{"title":"大盘肥肠鸡手工面","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/主食/大盘肥肠鸡手工面.md","filePath":"cookbook/主食/大盘肥肠鸡手工面.md"}'),t={name:"cookbook/主食/大盘肥肠鸡手工面.md"},e=o('<h1 id="大盘肥肠鸡手工面" tabindex="-1">大盘肥肠鸡手工面 <a class="header-anchor" href="#大盘肥肠鸡手工面" aria-label="Permalink to &quot;大盘肥肠鸡手工面&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>鸡块（鸡边腿）</li><li>肥肠</li><li>肥肠鸡酱料（火锅底料、红油豆瓣、牛油、大豆油、菜籽油、干红椒、八角、陈皮、山奈等）（老乡鸡中央厨房切配、炒制，详细成分、配比与步骤老乡鸡官方未公布，请依据个人口味适量调整）</li><li>肥肠鸡泡菜包（白萝卜、豇豆、大叶芥菜、二荆条、野山椒）</li><li>芋艿</li><li>香菜</li><li>生姜</li><li>蒜子</li><li>京葱</li><li>熟猪油</li><li>大豆油</li><li>芝麻香油</li><li>花椒油</li><li>干青花椒</li><li>干红辣椒</li><li>白芝麻</li><li>面胚</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><ul><li><p>鸡块制作：</p></li><li><p>1.下入350g 菜籽油、200g 猪油、清洗好沥干水分的3200g鸡块，煸炒至表皮微黄；</p></li><li><p>2.下入50g 葱、140g 姜、200g 蒜，炒出香味；</p></li><li><p>3.下入800g 肥肠鸡酱料，翻炒均匀；</p></li><li><p>4.下入3000g 水、40g 盐，煮制2 分钟即可倒出，鸡块和汤备用。</p></li><li><p>面片制作：</p></li><li><ol><li>解冻完全的每个面胚扯成30-35cm 长的面片；</li></ol></li><li><ol start="2"><li>放入沸水中，煮制2 分钟，放入餐具中；</li></ol></li><li><ol start="3"><li>锅里放入450g 炒好的鸡块、70g 焯水的芋艿、40g 肥肠，烧开煮3 分钟；</li></ol></li><li><ol start="4"><li>再下入8g 线椒、5g 鲜红小米辣、10g 芝麻香油、5g 花椒油，继续煮制1 分钟，关火倒入餐具中；</li></ol></li><li><ol start="5"><li>20g 大豆油烧热，加入2g 干青花椒、5g 干红辣椒段，炸出香味后均匀淋到菜上；</li></ol></li><li><ol start="6"><li>最后点缀5g 白芝麻、5g 香菜段出品。</li></ol></li></ul>',5),r=[e];function _(s,c,n,p,d,g){return a(),i("div",null,r)}const m=l(t,[["render",_]]);export{u as __pageData,m as default};
