import{_ as l,c as i,o as a,a2 as o}from"./chunks/framework.wERSFrVX.js";const m=JSON.parse('{"title":"香辣鸡丁拌面","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/主食/香辣鸡丁拌面.md","filePath":"cookbook/主食/香辣鸡丁拌面.md"}'),t={name:"cookbook/主食/香辣鸡丁拌面.md"},e=o('<h1 id="香辣鸡丁拌面" tabindex="-1">香辣鸡丁拌面 <a class="header-anchor" href="#香辣鸡丁拌面" aria-label="Permalink to &quot;香辣鸡丁拌面&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>手擀面或鲜面</li><li>鸡丁（去骨鸡腿肉）</li><li>香干丁</li><li>鲜红小米辣</li><li>线椒</li><li>蒜子</li><li><a href="/配料/炒菜基料.html">炒菜基料</a></li><li>大豆油</li><li>底料（酱油、水、花椒油、大蒜等）（来自成都圣恩生物）</li><li>拌菜红油</li><li>芝麻酱</li><li>青菜</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><ul><li><ol><li>香辣鸡丁炒制： ①200g 大豆油烧至180℃，下入700g 鸡丁炒熟； ②下入60g 拍蒜、100g 鲜红小米辣炒香； ③下入110g 炒菜基料翻炒均匀上色； ④下入400g 香干丁，煸炒1 分钟； ⑤下入200g 线椒圈，炒至断生，备用；</li></ol></li><li><ol start="2"><li>将275g 手擀面或130g 鲜面、25g 青菜在沸水中煮3 分钟；</li></ol></li><li><ol start="3"><li>面碗中加入15g 牛肉面汤底料、15g 拌菜红油、5g 蒜蓉、5g 熟猪油、15g 芝麻酱料，放入煮好的面条和青菜；</li></ol></li><li><ol start="4"><li>舀取125g 香辣鸡丁放于面条上，撒上5g 葱花出品。</li></ol></li></ul>',5),r=[e];function _(s,c,n,h,d,g){return a(),i("div",null,r)}const f=l(t,[["render",_]]);export{m as __pageData,f as default};
