import{_ as l,c as i,o as a,a2 as o}from"./chunks/framework.wERSFrVX.js";const m=JSON.parse('{"title":"毛豆烧土鸡","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/炒菜/毛豆烧土鸡.md","filePath":"cookbook/炒菜/毛豆烧土鸡.md"}'),t={name:"cookbook/炒菜/毛豆烧土鸡.md"},e=o('<h1 id="毛豆烧土鸡" tabindex="-1">毛豆烧土鸡 <a class="header-anchor" href="#毛豆烧土鸡" aria-label="Permalink to &quot;毛豆烧土鸡&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>鸡块（雪山草、小母鸡、鸡边腿）</li><li>豆米</li><li>菜籽油</li><li>熟猪油</li><li>姜片</li><li>拍蒜</li><li>剁椒</li><li>皮冻</li><li>毛豆烧鸡料（来自四川新雅轩、成都圣恩、青岛日辰）</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><ul><li><ol><li>锅中混合 130g 菜籽油和 40g 熟猪油，烧至油温 180℃；</li></ol></li><li><ol start="2"><li>倒入 1500g 鸡块，煸炒至鸡肉表面泛黄；</li></ol></li><li><ol start="3"><li>鸡肉中加入 60g 蒜子、60g 姜片、50g 剁椒翻炒;</li></ol></li><li><ol start="4"><li>炒香后放入 150g 毛豆烧鸡料炒至鸡块上色；</li></ol></li><li><ol start="5"><li>加入 120g 皮冻、280g 水一起烧开；</li></ol></li><li><ol start="6"><li>倒入 750g 豆米烧制 3 分钟后大火收汁，出锅前加入 30g 葱段翻拌均匀。</li></ol></li></ul>',5),r=[e];function s(_,c,n,d,h,u){return a(),i("div",null,r)}const f=l(t,[["render",s]]);export{m as __pageData,f as default};
