import{_ as a,c as l,o as i,a2 as e}from"./chunks/framework.wERSFrVX.js";const b=JSON.parse('{"title":"笋子鸡丁盖饭","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/炒菜/笋子鸡丁盖饭.md","filePath":"cookbook/炒菜/笋子鸡丁盖饭.md"}'),o={name:"cookbook/炒菜/笋子鸡丁盖饭.md"},t=e('<h1 id="笋子鸡丁盖饭" tabindex="-1">笋子鸡丁盖饭 <a class="header-anchor" href="#笋子鸡丁盖饭" aria-label="Permalink to &quot;笋子鸡丁盖饭&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>鸡丁（去骨鸡腿肉）</li><li><a href="/配料/剁椒酱.html">剁椒酱</a></li><li>罗汉笋块</li><li>茶叶蛋</li><li>菜苔</li><li>豉油汁（大豆油、酱油、白砂糖、洋葱、大葱）</li><li>盐</li><li>鸡精</li><li>老抽</li><li>蒜花</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><h3 id="_1-剁椒鸡丁炒制" tabindex="-1">1. 剁椒鸡丁炒制： <a class="header-anchor" href="#_1-剁椒鸡丁炒制" aria-label="Permalink to &quot;1. 剁椒鸡丁炒制：&quot;">​</a></h3><ul><li>①180g 大豆油烧至 180℃、下入 700g 鸡丁炒熟；</li><li>②下入 8g 老抽、5g 鸡精、2g 盐，将鸡丁炒制上色；</li><li>③下入 120g 剁椒酱、300g 焯水罗汉笋块，一起炒香；</li><li>④下入 36g 蒜花翻炒均匀，备用；</li></ul><h3 id="_2-菜苔烫制" tabindex="-1">2. 菜苔烫制 <a class="header-anchor" href="#_2-菜苔烫制" aria-label="Permalink to &quot;2. 菜苔烫制&quot;">​</a></h3><ul><li>取 70g 菜苔，在沸水中烫 1 分钟，备用；</li></ul><h3 id="_3-装配" tabindex="-1">3. 装配 <a class="header-anchor" href="#_3-装配" aria-label="Permalink to &quot;3. 装配&quot;">​</a></h3><ul><li>餐盒中放入 300g 米饭，将 170g 剁椒鸡丁、70g 菜苔、半个卤蛋放在米饭上，菜苔上淋入 15g 豉油汁出品。</li></ul>',10),r=[t];function _(h,n,c,s,d,u){return i(),l("div",null,r)}const f=a(o,[["render",_]]);export{b as __pageData,f as default};
