import{_ as l,c as i,o as a,a2 as e}from"./chunks/framework.wERSFrVX.js";const m=JSON.parse('{"title":"肥肠鸡","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/炒菜/肥肠鸡.md","filePath":"cookbook/炒菜/肥肠鸡.md"}'),o={name:"cookbook/炒菜/肥肠鸡.md"},t=e('<h1 id="肥肠鸡" tabindex="-1">肥肠鸡 <a class="header-anchor" href="#肥肠鸡" aria-label="Permalink to &quot;肥肠鸡&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>鸡块（鸡边腿）</li><li>肥肠（猪大肠、黄酒、青花椒、大豆油、冰糖、小茴香、草果、白胡椒、十三香、二锅头等）（来自牧原肉食品）</li><li>生姜</li><li>蒜子</li><li>京葱</li><li><a href="/配料/肥肠鸡酱料.html">肥肠鸡酱料</a></li><li>熟猪油</li><li>大豆油</li><li>肥肠鸡泡菜包（白萝卜、豇豆、大叶芥菜、二荆条、野山椒）（来自四川民福记）</li><li>配菜（莴笋、土豆、香菜、芋艿、线椒、鲜红小米辣等）</li><li>宽粉</li><li>芝麻香油</li><li>花椒油</li><li>干青花椒</li><li>干红辣椒</li><li>白芝麻</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><h3 id="鸡块制作" tabindex="-1">鸡块制作： <a class="header-anchor" href="#鸡块制作" aria-label="Permalink to &quot;鸡块制作：&quot;">​</a></h3><ul><li>1.下入 350g 菜籽油、200g 猪油、清洗好沥干水分的 3200g 鸡块，煸炒至表皮微黄；</li><li>2.下入 50g 葱、140g 姜、200g 蒜，炒出香味；</li><li>3.下入 800g 肥肠鸡酱料，翻炒均匀；</li><li>4.下入 3000g 水、40g 盐，煮制 2 分钟即可倒出，鸡块和汤备用。</li></ul><h3 id="配菜制作" tabindex="-1">配菜制作： <a class="header-anchor" href="#配菜制作" aria-label="Permalink to &quot;配菜制作：&quot;">​</a></h3><ul><li>1.锅中加入适量的水和盐，烧开后倒入蔬菜；</li><li>2.再次烧开后煮制 1 分钟，捞出冲凉备用。</li></ul><h3 id="肥肠鸡制作" tabindex="-1">肥肠鸡制作： <a class="header-anchor" href="#肥肠鸡制作" aria-label="Permalink to &quot;肥肠鸡制作：&quot;">​</a></h3><ul><li>1.将泡发好的 50g 宽粉和焯水后的 60g 土豆片、80g 莴笋片烫制20 秒，沥干水分放入盘中；</li><li>2.锅中放入 200g 煮制鸡块、80g 肥肠、70g 芋艿、250g 汤、50g泡菜，煮制 3 分钟；</li><li>3.再下入 8g 线椒、5g 鲜红小米辣、10g 芝麻香油、5g 花椒油，继续煮制 1 分钟，关火倒入盘中；</li><li>4.向 20g 热大豆油加入 2g 干青花椒、5g 干红辣椒段，炸出糊辣味后均匀淋到菜上；</li><li>5.最后点缀 5g 白芝麻、5g 香菜段，即可出品。</li></ul>',10),r=[t];function h(n,c,s,d,_,g){return a(),i("div",null,r)}const b=l(o,[["render",h]]);export{m as __pageData,b as default};
