import{_ as l,c as a,o as i,a2 as o}from"./chunks/framework.wERSFrVX.js";const f=JSON.parse('{"title":"嗨嗨锅（泡椒鸡米花米线）","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/煮锅/嗨嗨锅（泡椒鸡米花米线）.md","filePath":"cookbook/煮锅/嗨嗨锅（泡椒鸡米花米线）.md"}'),t={name:"cookbook/煮锅/嗨嗨锅（泡椒鸡米花米线）.md"},e=o('<h1 id="嗨嗨锅-泡椒鸡米花米线" tabindex="-1">嗨嗨锅（泡椒鸡米花米线） <a class="header-anchor" href="#嗨嗨锅-泡椒鸡米花米线" aria-label="Permalink to &quot;嗨嗨锅（泡椒鸡米花米线）&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>米线</li><li>西红柿</li><li>娃娃菜</li><li>木耳</li><li>海带丝</li><li>鸡米花</li><li>骨汤调味料（水、猪油、白砂糖、生姜、大葱等）（来自成都圣恩生物）</li><li>泡椒调味料（泡红椒、泡小米辣、泡姜、泡萝卜、白砂糖等）（来自成都圣恩生物）</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><ul><li><ol><li>10 份泡椒骨汤汤底调制：2900g 开水中加入140g 骨汤调味料、700g 泡椒调味料，搅拌均匀备用；</li></ol></li><li><ol start="2"><li>奶锅中加入370g 泡椒骨汤汤底烧开；</li></ol></li><li><ol start="3"><li>下入50g 西红柿片、50g 娃娃菜、20g 木耳丝，20g 海带丝烧开后煮2 分钟，倒入红底座锅中；</li></ol></li><li><ol start="4"><li>将250g 湿米线在沸水中煮2 分30 秒后，倒入红底座锅中，放入60g 炸制好的鸡米花出品。</li></ol></li></ul>',5),_=[e];function r(s,c,n,d,h,u){return i(),a("div",null,_)}const g=l(t,[["render",r]]);export{f as __pageData,g as default};
