import{_ as l,c as a,o,a2 as i}from"./chunks/framework.wERSFrVX.js";const f=JSON.parse('{"title":"嗨嗨锅（酸菜鱼米线）","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/煮锅/嗨嗨锅（酸菜鱼米线）.md","filePath":"cookbook/煮锅/嗨嗨锅（酸菜鱼米线）.md"}'),t={name:"cookbook/煮锅/嗨嗨锅（酸菜鱼米线）.md"},e=i('<h1 id="嗨嗨锅-酸菜鱼米线" tabindex="-1">嗨嗨锅（酸菜鱼米线） <a class="header-anchor" href="#嗨嗨锅-酸菜鱼米线" aria-label="Permalink to &quot;嗨嗨锅（酸菜鱼米线）&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>米线</li><li>小叶酸菜</li><li>巴沙鱼片</li><li>豆米骨汤调味料（豆米、猪油、白砂糖、猪骨等）（来自浙江今味）</li><li>泡椒调味料（来自成都圣恩生物）</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><ul><li><ol><li>10 份米线汤底调制：3500g 开水中加入400g 豆米骨汤调味料、350g 泡椒调味料，搅拌均匀备用；</li></ol></li><li><ol start="2"><li>奶锅中加入420g 米线汤底烧开，下入70g 小叶酸菜，烧开；</li></ol></li><li><ol start="3"><li>下入120g 鱼片烧开后煮2 分钟，倒入红底座锅中；</li></ol></li><li><ol start="4"><li>将250g 湿米线在沸水中煮2 分30 秒后，倒入红底座锅中出品。</li></ol></li></ul>',5),r=[e];function _(s,c,n,d,h,u){return o(),a("div",null,r)}const k=l(t,[["render",_]]);export{f as __pageData,k as default};
