import{_ as l,c as i,o as a,a2 as o}from"./chunks/framework.wERSFrVX.js";const m=JSON.parse('{"title":"金汤酸菜鱼（大份）","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/煮锅/金汤酸菜鱼（大份）.md","filePath":"cookbook/煮锅/金汤酸菜鱼（大份）.md"}'),e={name:"cookbook/煮锅/金汤酸菜鱼（大份）.md"},t=o('<h1 id="金汤酸菜鱼-大份" tabindex="-1">金汤酸菜鱼（大份） <a class="header-anchor" href="#金汤酸菜鱼-大份" aria-label="Permalink to &quot;金汤酸菜鱼（大份）&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>娃娃菜</li><li>粉丝</li><li>黑鱼片</li><li>金汤（来自成都圣恩生物、四川民福记、广东美味源）</li><li>小叶酸菜</li><li>大豆油</li><li>干红椒</li><li>干青花椒</li><li>蒜</li><li>芝麻</li><li>葱</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><ul><li><ol><li>底汤调配（5 份）： 2480g 白开水、350g 金汤，搅拌均匀备用；</li></ol></li><li><ol start="2"><li>80g 娃娃菜和泡发好的 85g 粉丝下入沸水中汆烫 10 秒，捞起沥水后倒入装有 80g 小叶酸菜的餐具中，再将 150g 黑鱼片下入沸水中汆烫 30 秒，捞起沥水后平铺在娃娃菜、玉耳上，倒入 500g 底汤；</li></ol></li><li><ol start="3"><li>锅中倒入 20g 大豆油烧至 120℃，关火后下入 5g 干红椒、2g 干青花椒、5g 蒜末爆香 2 秒，倒入黑鱼片上，出品撒1g 芝麻和 1g 葱花点缀。</li></ol></li></ul>',5),r=[t];function _(s,c,n,d,h,u){return a(),i("div",null,r)}const f=l(e,[["render",_]]);export{m as __pageData,f as default};
