import{_ as l,c as a,o as i,a2 as e}from"./chunks/framework.wERSFrVX.js";const m=JSON.parse('{"title":"酸菜肥肠煲","description":"","frontmatter":{},"headers":[],"relativePath":"cookbook/砂锅菜/酸菜肥肠煲.md","filePath":"cookbook/砂锅菜/酸菜肥肠煲.md"}'),o={name:"cookbook/砂锅菜/酸菜肥肠煲.md"},t=e('<h1 id="酸菜肥肠煲" tabindex="-1">酸菜肥肠煲 <a class="header-anchor" href="#酸菜肥肠煲" aria-label="Permalink to &quot;酸菜肥肠煲&quot;">​</a></h1><h2 id="配料" tabindex="-1">配料 <a class="header-anchor" href="#配料" aria-label="Permalink to &quot;配料&quot;">​</a></h2><ul><li>肥肠</li><li>猪杂（肥肠、猪肚、猪肺等）</li><li>泡小米辣</li><li>蒜子</li><li>泡椒调味料（来自成都圣恩）</li><li>炒制酸菜</li><li>鸡精</li><li>白砂糖</li><li>老抽</li><li>陈醋</li><li>藤椒油</li><li>蒜花</li><li>水</li><li>菜籽油</li></ul><h2 id="步骤" tabindex="-1">步骤 <a class="header-anchor" href="#步骤" aria-label="Permalink to &quot;步骤&quot;">​</a></h2><h3 id="_1-酸菜肥肠半成品烧制-5-份" tabindex="-1">1. 酸菜肥肠半成品烧制（5 份）： <a class="header-anchor" href="#_1-酸菜肥肠半成品烧制-5-份" aria-label="Permalink to &quot;1. 酸菜肥肠半成品烧制（5 份）：&quot;">​</a></h3><ul><li>① 150g 菜籽油烧至 160℃；</li><li>② 下入 50g 拍蒜、50g 泡小米辣，炒香；</li><li>③ 下入 250g 泡椒调味料，炒香；</li><li>④ 下入 300g 焯水后的肥肠、250g 焯水后的猪杂，炒香；</li><li>⑤ 下入 1200g 水，烧开后，下入 5g 白糖、10g 老抽、10g陈醋、8g 藤椒油，煮 3 分钟备用。</li></ul><h3 id="_2-装配" tabindex="-1">2. 装配： <a class="header-anchor" href="#_2-装配" aria-label="Permalink to &quot;2. 装配：&quot;">​</a></h3><ul><li>将 80g 小叶酸菜平铺在砂锅底部，放入 230g 烧制后的肥肠，烧开后煮 1 分钟，撒 10g 蒜花出品。</li></ul>',8),r=[t];function _(n,c,h,s,d,u){return i(),a("div",null,r)}const b=l(o,[["render",_]]);export{m as __pageData,b as default};
